	if (!i) {
		cb_warning (COBC_WARN_FILLER,
			_("alphanumeric literal has zero length; a SPACE will be assumed"));
		plexbuff[i++] = ' ';
	} else if (i > cb_lit_length) {
		i = cb_lit_length;
	}

